import {Component, Input} from '@angular/core';
import {ControlValueAccessor, NG_VALUE_ACCESSOR} from '@angular/forms';
import {CacheImage} from '../../../library/cache-image';
import {ServerService} from '../../../service/server/server.service';

@Component({
  selector: 'app-game-cover-input',
  templateUrl: './game-cover-input.component.html',
  styleUrl: './game-cover-input.component.scss',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      multi: true,
      useExisting: GameCoverInputComponent,
    },
  ],
})
export class GameCoverInputComponent implements ControlValueAccessor {
  fallback =
    'data:image/png;base64,';

  value: string;
  disable = false;
  isVisible = false;
  cacheUrl = '';
  cacheImage: CacheImage | null = null;

  @Input()
  searchKeyword: string;

  @Input()
  gameId!: string;

  constructor(
    public serverService: ServerService
  ) {
    this.value = '';
    this.searchKeyword = '';
  }

  onChange = (_: string | null) => { };
  onTouch = () => { };

  writeValue(obj: string): void {
    this.value = obj;
    this.cacheImage = new CacheImage(obj, this.serverService);
  }
  registerOnChange(fn: any): void {
    this.onChange = fn;
  }
  registerOnTouched(fn: any): void {
    this.onTouch = fn;
  }
  setDisabledState?(isDisabled: boolean): void {
    this.disable = isDisabled;
  }

  openImageSelectDialog() {
    this.isVisible = true;
  }

  async confirm() {
    this.isVisible = false;
    this.value = this.cacheUrl;
    this.writeValue(this.cacheUrl);
    this.onChange(this.cacheUrl);
  }
}
